mapper.select:
select ia.id id,ia.`name` name,ia.type type,ia.description description,ia.address address,ia.telephone telephone,ia.postcode postcode,ia.created_time created_time,ia.updated_time updated_time ,o.name organizationName ,ia.organization_id from ba_area ia left join xt_organization o on ia.organization_id=o.id where 1=1
and ${dataAuth}
and ia.name like '%${name}%'
and ia.type ='${type}'
limit ${pageStart},${rows}

		select ia.* ,o.name  organization_name
        from ba_area ia left join xt_organization o on ia.organization_id=o.id
         where ia.id = #{id}

		select ia.* ,o.name  organization_name
        from ba_area ia left join xt_organization o on ia.organization_id=o.id
         where ia.id = #{id}
