mapper.update:
update ba_sign_file
set serial_id = #{serialId,jdbcType=INTEGER},
  file_type =  #{fileType,jdbcType=INTEGER},
  file_name = #{fileName,jdbcType=VARCHAR},
  creat_time = #{creatTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from ba_sign_file
where serial_id = #{serialId,jdbcType=BIGINT}
and file_type = #{fileType,jdbcType=BIGINT}
order by creat_time DESC limit 0,1
