SELECT count(DISTINCT(u.id)) count FROM xt_user u LEFT JOIN xt_role r ON u.role_id = r.id LEFT JOIN xt_organization org ON org.id = u.organization_id LEFT JOIN xt_code xc ON xc.code_key= u.certificate_type_id LEFT JOIN ba_area ia on ia.organization_id = org.id
1 = 1 and u.type is not null
and ${dataAuth}
and ${dataAuth1}
and u.login_name like '%${login_name}%'
and u.real_name like '%${real_name}%'
and u.job_title like '%${job_title}%'
and u.mobile like '%${mobile}%'
and u.certificate_type_id like '%${certificate_type_id}%'
and u.certificate_no like '%${certificate_no}%'
and org.name like '%${organization_name}%'
and org.type like '%${organization_type}%'
and u.is_active like '%${isActive}%'
and u.created_time >=#{start_date}
and u.created_time <=#{end_date}
and r.id = #{role}

SELECT count(DISTINCT(u.id)) count FROM xt_user u LEFT JOIN xt_role r ON u.role_id = r.id LEFT JOIN xt_organization org ON org.id = u.organization_id LEFT JOIN xt_code xc ON xc.code_key= u.certificate_type_id LEFT JOIN ba_area ia on ia.organization_id = org.id
1 = 1 and u.type is not null and u.real_name not like '%管理员%'
and ${dataAuth}
and ${dataAuth1}
and u.login_name like '%${login_name}%'
and u.real_name like '%${real_name}%'
and u.job_title like '%${job_title}%'
and u.mobile like '%${mobile}%'
and u.certificate_type_id like '%${certificate_type_id}%'
and u.certificate_no like '%${certificate_no}%'
and org.name like '%${organization_name}%'
and org.type like '%${organization_type}%'
and u.is_active like '%${isActive}%'
and u.created_time >=#{start_date}
and u.created_time <=#{end_date}
and r.id = #{role}

    SELECT *,o.id organization_id,o.org_code organization_code FROM xt_user u
    LEFT JOIN xt_organization o ON  u.organization_id = o.id
    WHERE 1=1

      and u.id not in (select user_id from jz_jzg_gx where jzg_gmxx_id=#{jzgGmxxId})


      and	u.real_name like '%${realName}%'


		  and u.identity=#{political}


