SELECT u.id, u.login_name, u.real_name, u.job_title, u. password, u.mobile, u.email, u.certificate_type_id, u.certificate_no, u.sex, u.is_active, u.description, u.created_time, u.updated_time, u.type, ct.code_value certificate_name, org.name AS organization_name FROM xt_user AS u LEFT JOIN xt_code ct ON u.certificate_type_id = ct.code_key and ct.type = 'ZJZLID' LEFT JOIN xt_organization AS org ON org.id = u.organization_id LEFT JOIN ba_area ia ON ia.organization_id = org.id
and u.login_name like '%${login_name}%'
and u.real_name like '%${real_name}%'
and u.job_title like '%${job_title}%'
and u.mobile like '%${mobile}%'
and u.certificate_type_id like '%${certificate_type_id}%'
and u.certificate_no like '%${certificate_no}%'
and u.is_active like '%${isActive}%'
and u.created_time >=#{start_date}
and u.created_time <=#{end_date}
and u.type =#{type}
and org.name = #{organization_name}
and ${dataAuth}
order by u.id desc
limit ${pageStart},${rows}

