SELECT e.id AS id,e.case_no AS caseNo,e.case_name AS caseName,e.informant,e.suspect,e.case_type AS caseType,e.batch_item_id,e.case_desc,e.oraganizationId,o.name orgname,e.leader_id,e.police_id,e.implementation,e.accordwith,e.remarks,e.scorevalue,e.pointsvalue from evaluation e,xt_organization o WHERE e.oraganizationId = o.id
and e.case_no = #{caseNo}
and e.id = #{caseId}
ORDER BY e.examine_time DESC LIMIT 0,1

