mapper.update:
update sa_personal_config_detail set
param_key=#{paramKey} ,param_value=#{paramValue} ,`desc`=#{desc}
where id=#{id}

mapper.select:
SELECT d.* FROM sa_personal_config_detail d
LEFT JOIN sa_personal_config c ON c.id=d.personal_config_id
WHERE c.type=#{codeKey} AND d.param_key=#{paramKey}
AND c.warehouse_id=#{areaId}

SELECT * FROM sa_personal_config_detail
WHERE TYPE=1 AND personal_config_id=(
SELECT personal_config_id FROM sa_personal_config_detail WHERE id=#{id}
)

SELECT * FROM sa_personal_config_detail d WHERE d.personal_config_id = ( SELECT c.id FROM sa_personal_config c WHERE 1=1
c.warehouse_id = #{warehouseId}
AND c.desc = #{name} and c.is_enable = 1);
