mapper.select:
select d.*, a.name as warehouseName ,l.name locationName
from sa_position_device d,sa_warehouse a,sa_location l ,xt_organization o
where a.id=d.warehouse_id and l.id=d.location_id AND a.org_id = o.id

    and ${dataAuth}


    and d.device_no like '%${deviceNo}%'


    and d.warehouse_id =${warehouseId}


    and d.location_id =${location}

order by d.created_time desc limit ${pageStart},${rows}

select count(1) from sa_position_device d,sa_warehouse a,xt_organization o where a.id=d.warehouse_id AND
a.org_id = o.id

    and ${dataAuth}


    and d.device_no like '%${deviceNo}%'


    and d.warehouse_id =${warehouseId}
